import { Box, FAB, ListItem, Stack } from '@react-native-material/core';
import Icon from '@expo/vector-icons/MaterialCommunityIcons';
import { SafeAreaView } from 'react-native-safe-area-context';
import { FlatList, ScrollView, StyleSheet, Text, View } from 'react-native';
import Constants from 'expo-constants';
const styles = StyleSheet.create({
  list: {
    paddingHorizontal: 20,
  },

  listItem: {
    backgroundColor: '#EEE',
    marginTop: 20,
    padding: 30,
  },
});

const Home = ({ navigation: { navigate } }: any) => {
  function handleNavigationForm() {
    navigate('Form', { names: ['Brent', 'Satya', 'Michaś'] });
  }

  const state = {
    data: [
      { id: 0, full_name: 'Repo 1' },
      { id: 1, full_name: 'Repo 2' },
      { id: 2, full_name: 'Repo 3' },
      { id: 3, full_name: 'Repo 4' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
      { id: 4, full_name: 'Repo 5' },
    ],
  };

  const renderItem = ({ item }) => (
    <View style={styles.listItem}>
      <Text>{item.full_name}</Text>
    </View>
  );

  return (
    <SafeAreaView style={{ flex: 1 }}>
      <Box
        w={'100%'}
        h={'100%'}
        style={{
          flex: 1,
        }}
      >
        {/* Main */}
        <Box
          style={{
            flex: 1,
          }}
        >
          <FlatList
            style={{ marginTop: 30 }}
            contentContainerStyle={styles.list}
            data={state.data}
            renderItem={renderItem}
            keyExtractor={(item) => item.id}
          />
        </Box>

        {/* Footer */}
        <Box
          style={{
            alignItems: 'flex-end',
            padding: 8,
            backgroundColor: 'transparent',
            position: 'absolute',
          }}
        >
          <Stack fill center spacing={4}>
            <FAB
              onPress={handleNavigationForm}
              icon={(props) => <Icon name="plus" {...props} />}
            />
          </Stack>
        </Box>
      </Box>
    </SafeAreaView>
  );
};

export default Home;
